* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  font-size: 62.5%;
}
@media only screen and (max-width: 900px) {
  html {
    font-size: 55%;
  }
}
@media only screen and (max-width: 700px) {
  html {
    font-size: 50%;
  }
}
@media only screen and (max-width: 700px) {
  html {
    font-size: 45%;
  }
}

body {
  font-family: "Lato", sans-serif;
  color: white;
}

.--u-letter-underline {
  display: inline-block;
  padding-bottom: 1rem;
  border-bottom: 3px solid #ffaf29;
}

.hamburger {
  cursor: pointer;
  height: 3.5rem;
  width: 3.5rem;
  border: 2px solid white;
  display: none;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  transition: all 0.3s;
}
@media only screen and (max-width: 900px) {
  .hamburger {
    display: flex;
  }
}
.hamburger span {
  width: 2.4rem;
  display: block;
  border-bottom: 2px solid white;
  margin: 0.3rem 0;
}
.hamburger:hover {
  border: 2px solid #ffb129;
}
.hamburger:hover span {
  border-bottom: 2px solid #ffb1299c;
}

.hamburger-mob {
  position: absolute;
  right: 0;
  top: 0;
}

.modal {
  width: 100vw;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  background: rgba(0, 0, 0, 0.568);
  z-index: 10;
  display: none;
}
.modal__container {
  width: 50%;
  height: 80%;
  background: black;
  margin: auto;
  position: relative;
  top: 10rem;
  padding: 2rem;
}
@media only screen and (max-width: 900px) {
  .modal__container {
    width: 70%;
  }
}
@media only screen and (max-width: 700px) {
  .modal__container {
    width: 80%;
  }
}
@media only screen and (max-width: 600px) {
  .modal__container {
    width: 90%;
  }
}
.modal__content {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  grid-template-rows: repeat(12, 1fr);
  align-items: center;
  height: 100%;
  width: 100%;
}
.modal__content--exit {
  grid-row: 1/2;
  grid-column: 12/13;
  font-size: 1.8rem;
}
.modal__content--heading {
  grid-column: 1/-1;
  grid-row: 1/2;
  text-align: center;
  font-size: 3rem;
  padding-top: 1rem;
}
.modal__content--lead {
  grid-column: 4/10;
  grid-row: 3/4;
  font-size: 3rem;
  font-weight: 100;
  margin: 2rem 0 0 0;
}
.modal__content--features {
  grid-column: 4/10;
  grid-row: 4/10;
}
.modal__content--features h2 {
  font-size: 1.5rem;
  font-weight: 300;
  margin-bottom: 1rem;
}
.modal__content--features p {
  font-size: 1.4rem;
  font-weight: 300;
  line-height: 1.3;
}
.modal__content--github {
  grid-column: 4/5;
  grid-row: 11/12;
}
.modal__content--git-text {
  grid-column: 5/7;
  grid-row: 11/12;
  font-size: 1.2rem;
  font-weight: 300;
  position: relative;
  left: -2rem;
}
.modal__content--view {
  grid-column: 7/8;
  grid-row: 11/12;
}
.modal__content--view-text {
  grid-column: 8/10;
  grid-row: 11/12;
  font-size: 1.2rem;
  font-weight: 300;
  position: relative;
  left: -2rem;
}
.modal__content--1 {
  display: none;
}
.modal__content--2 {
  display: none;
}
.modal__content--3 {
  display: none;
}
.modal__content--4 {
  display: none;
}
.modal__content--5 {
  display: none;
}
.modal__content--6 {
  display: none;
}
.modal__content--7 {
  display: none;
}
.modal__content--8 {
  display: none;
}
.modal__content--9 {
  display: none;
}

.navbar {
  position: fixed;
  z-index: 2;
  height: 8rem;
  background-color: black;
  width: 100%;
  transition: all 1s;
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 1.6rem;
  padding: 0 1rem;
}
.navbar__navbrand {
  margin: 1rem;
}
.navbar__navbrand--link {
  text-decoration: none;
  color: white;
  transition: all 0.2s;
}
.navbar__navbrand--link:hover {
  color: #ffb129;
}
.navbar__list {
  list-style: none;
  display: flex;
}
@media only screen and (max-width: 900px) {
  .navbar__list {
    display: none;
  }
}
.navbar__list--item {
  margin: 1rem;
}
.navbar__list--link {
  text-decoration: none;
  color: white;
  transition: all 0.2s;
}
.navbar__list--link:hover {
  color: #ffb129;
}

.mobile-nav {
  width: 100%;
  height: 0;
  background-color: black;
  font-size: 1.6rem;
  position: fixed;
  z-index: 6;
  padding: 1rem;
}
.mobile-nav__list {
  display: none;
  list-style: none;
  margin-left: 1rem;
  position: relative;
}
.mobile-nav__list--item {
  margin-bottom: 1rem;
}
.mobile-nav__list--link {
  color: white;
  text-decoration: none;
}
.mobile-nav__list--link:hover {
  color: #ffb129;
}

.about {
  height: 100vh;
  background-color: #010304;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 1fr;
}
@media only screen and (max-width: 600px) {
  .about {
    grid-template-rows: repeat(2, 1fr);
  }
}
.about__desc {
  font-size: 1.6rem;
  font-weight: 300;
  margin-top: 2rem;
}
.about__overview {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: baseline;
  position: relative;
  left: 25%;
}
@media only screen and (max-width: 600px) {
  .about__overview {
    grid-row: 1/2;
    grid-column: 1/-1;
    left: 0;
  }
}
.about__overview--icons {
  margin-top: 4rem;
  display: flex;
  justify-content: start;
}
.about__overview--icon {
  height: 3rem;
  width: 3rem;
  fill: white;
  margin-right: 1rem;
}
.about__title {
  font-size: 5rem;
  font-weight: 100;
}
.about__title--top {
  display: block;
}
.about__title--bottom {
  display: block;
}
.about__content {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  grid-template-rows: repeat(12, 1fr);
  justify-items: center;
  align-items: center;
}
@media only screen and (max-width: 600px) {
  .about__content {
    grid-row: 2/3;
    grid-column: 1/-1;
  }
}
.about__content span {
  font-size: 1.6rem;
  padding-bottom: 0.5rem;
  cursor: pointer;
}
.about__content--intro {
  grid-row: 2/3;
  grid-column: 4/5;
}
@media only screen and (max-width: 600px) {
  .about__content--intro {
    grid-row: 2/3;
    grid-column: 1/2;
  }
}
.about__content--intro:hover {
  border-bottom: 2px solid #ffb1299c;
}
.about__content--contact {
  grid-row: 2/3;
  grid-column: 6/7;
}
@media only screen and (max-width: 600px) {
  .about__content--contact {
    grid-row: 2/3;
    grid-column: 3/4;
  }
}
.about__content--contact:hover {
  border-bottom: 2px solid #ffb1299c;
}
.about__content--skills {
  grid-row: 2/3;
  grid-column: 8/9;
}
@media only screen and (max-width: 600px) {
  .about__content--skills {
    grid-row: 2/3;
    grid-column: 5/6;
  }
}
.about__content--skills:hover {
  border-bottom: 2px solid #ffb1299c;
}
.about__card-intro {
  font-weight: 100;
}
.about__card-intro h2 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 300;
}
.about__card-intro :nth-child(2) {
  font-size: 3.5rem;
  margin-bottom: 3rem;
}
.about__card-intro :nth-child(3) {
  font-size: 1.5rem;
  font-weight: 300;
}
.about__card-intro :nth-child(5) {
  font-size: 1.5rem;
  font-weight: 300;
}
.about__card-contact {
  font-weight: 100;
  display: none;
}
.about__card-contact h2 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 300;
}
.about__card-contact :nth-child(2) {
  font-size: 3.5rem;
  margin-bottom: 3rem;
}
.about__card-contact :nth-child(3) {
  font-size: 1.5rem;
  font-weight: 300;
}
.about__card-contact :nth-child(4) {
  font-size: 1.5rem;
  font-weight: 300;
}

.active {
  border-bottom: 2px solid #ffaf29;
}

.card {
  grid-row: 3/11;
  grid-column: 4/9;
}
@media only screen and (max-width: 600px) {
  .card {
    grid-row: 4/-1;
    grid-column: 1/-1;
  }
}

.education {
  height: 100vh;
  background-color: #010304;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: 1fr;
}
@media only screen and (max-width: 600px) {
  .education {
    grid-template-rows: repeat(2, 1fr);
  }
}
.education__details {
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-left: 5rem;
  font-weight: 100;
}
@media only screen and (max-width: 600px) {
  .education__details {
    grid-row: 1/2;
    grid-column: 1/3;
    margin-left: 0;
  }
}
.education__details h2 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 300;
  margin-bottom: 2rem;
}
.education__details--blurb {
  font-size: 3.5rem;
  margin-bottom: 2.5rem;
}
.education__details--course {
  font-size: 1.4rem;
  font-weight: 300;
}

.education__game {
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  grid-template-rows: repeat(12, 1fr);
  justify-items: center;
  align-items: center;
}
@media only screen and (max-width: 600px) {
  .education__game {
    grid-row: 2/3;
    grid-column: 1/3;
  }
}
.education__game h2 {
  font-size: 1.5rem;
  font-weight: 300;
}
.education__game--new {
  font-size: 1.5rem;
  font-weight: 300;
}
.education__game--inst {
  cursor: pointer;
  grid-row: 2/3;
  grid-column: 8/9;
  font-size: 1.6rem;
  padding-bottom: 0.5rem;
}
@media only screen and (max-width: 600px) {
  .education__game--inst {
    grid-column: 7/8;
  }
}
.education__game--inst:hover {
  border-bottom: 2px solid #ffb1299c;
}
.education__game--game:hover {
  border-bottom: 2px solid #ffb1299c;
}
.education__game--game {
  cursor: pointer;
  grid-row: 2/3;
  grid-column: 6/7;
  font-size: 1.6rem;
  padding-bottom: 0.5rem;
}
@media only screen and (max-width: 600px) {
  .education__game--game {
    grid-column: 5/6;
  }
}

.card-ed {
  grid-row: 3/11;
  grid-column: 4/9;
}
@media only screen and (max-width: 600px) {
  .card-ed {
    grid-column: 1/-1;
  }
}

.game-container {
  display: grid;
  grid-template-rows: min-content 5rem min-content;
  grid-template-columns: repeat(12, 1fr);
  justify-items: center;
}

.instruction-container {
  display: none;
  flex-direction: column;
}
.instruction-container h2 {
  font-size: 1.5rem;
  font-weight: 300;
  margin-bottom: 2rem;
}
.instruction-container__blurb {
  font-size: 3rem;
  font-weight: 100;
  margin-bottom: 3rem;
}
.instruction-container__desc {
  font-size: 1.5rem;
  font-weight: 300;
  margin-bottom: 2rem;
}

.head-section {
  grid-column: 1/-1;
  grid-row: 2/3;
  width: 100%;
  font-size: 1rem;
  text-transform: uppercase;
  text-align: center;
}

.heading {
  margin: 1rem 0;
  font-weight: 300;
}

#color-display {
  text-align: center;
  margin-bottom: 4rem;
  font-size: 1rem;
  font-weight: 300;
}

.message {
  grid-column: 5/8;
  margin: auto;
  font-size: 1.4rem;
}

.winner {
  color: green;
}

.loser {
  color: red;
}

.game-board {
  grid-row: 3/4;
  grid-column: 3/11;
  display: grid;
  grid-template-rows: repeat(2, min-content);
  grid-template-columns: repeat(3, min-content);
  grid-gap: 2rem;
  margin: 5rem 0;
}

.circle {
  height: 8rem;
  width: 8rem;
  background-color: yellowgreen;
  border-radius: 50%;
  transition: all 1s;
}

.new-colors {
  cursor: pointer;
}

.new-colors:hover {
  border-bottom: 2px solid #ffb1299c;
}

.landing {
  background-image: url("https://images.unsplash.com/photo-1478760329108-5c3ed9d495a0?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=800&q=60");
  min-height: 100vh;
  background-attachment: fixed;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  transition: all;
}
.landing__title {
  font-size: 10rem;
}
.title-animation {
  animation: text-flicker-in-glow 3s;
}

.sub-animation {
  animation: text-flicker-in-glow 4s ease-out;
