.modal {
    width: 100vw;
    height: 100vh;
    position: fixed;
    left: 0;
    top: 0;
    background: rgba(0, 0, 0, 0.568);
    z-index: 10;
    display: none;

    &__container {
        width: 50%;
        height: 80%;
        background: black;
        margin: auto;
        position: relative;
        top: 10rem;
        padding: 2rem;
        // display: grid;
        // grid-template-columns: 1fr;
        // grid-template-rows: 1fr;

        @media only screen and (max-width: 900px) {
            width: 70%;
        }

        @media only screen and (max-width: 700px) {
            width: 80%;
        }

        @media only screen and (max-width: 600px) {
            width: 90%;
        }
    }

    &__content {
        //Set grid
        display: grid;
        grid-template-columns: repeat(12, 1fr);
        grid-template-rows: repeat(12, 1fr);
        align-items: center;
        height: 100%;
        width: 100%;

        &--exit {
            grid-row: 1 / 2;
            grid-column: 12 / 13;
            font-size: 1.8rem;
        }

        // &--img {
        //     width: 100%;
        //     height: 100%;
        //     grid-column: 4 / 10;
        //     grid-row: 2 / 7;
        // }

        &--heading{
            grid-column: 1 / -1;
            grid-row: 1 / 2;
            text-align: center;
            font-size: 3rem;
            padding-top: 1rem;
        }

        &--lead {
            grid-column: 4 / 10;
            grid-row: 3 / 4;
            font-size: 3rem;
            font-weight: 100;
            margin: 2rem 0 0 0;
        }

        &--features {
            grid-column: 4 / 10;
            grid-row: 4 / 10;

            h2 {
                font-size: 1.5rem;
                font-weight: 300;
                margin-bottom: 1rem;
            }

            p {
                font-size: 1.4rem;
                font-weight: 300;
                line-height: 1.3;
            }
        }

        &--github {
            grid-column: 4 / 5;
            grid-row: 11 / 12;
        }

        &--git-text {
            grid-column: 5 / 7;
            grid-row: 11 / 12;
            font-size: 1.2rem;
            font-weight: 300;
            position: relative;
            left: -2rem;
        }

        &--view {
            grid-column: 7 / 8;
            grid-row: 11 / 12;

        }

        &--view-text {
            grid-column: 8 / 10;
            grid-row: 11 / 12;
            font-size: 1.2rem;
            font-weight: 300;
            position: relative;
            left: -2rem;

        }

        &--1 {
            display: none;
        }

        &--2 {
            display: none;
        }

        &--3 {
            display: none;
        }

        &--4 {
            display: none;
        }

        &--5 {
            display: none;
        }

        &--6 {
            display: none;
        }

        &--7 {
            display: none;
        }

        &--8 {
            display: none;
        }

        &--9 {
            display: none;
        }
    }
}

