.about {
    min-height: 100vh;
    background-color: #010304;
    max-width: 100%;
    overflow: hidden;

    //Set grid
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: 1fr;

    //Change grid layout for 600px and below
    @media only screen and (max-width: 800px) {
        grid-template-rows: repeat(2, 1fr);
        padding: 5rem;
        grid-row-gap: 10rem;
    }


    //Description of developer
    &__desc {
        font-size: 1.6rem;
        font-weight: 300;
        margin-top: 2rem;

        @media only screen and (max-width: 800px) {
            font-size: 2rem;
            font-weight: normal;
        }
    }

