    &__overview {
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: baseline;
        position: relative;
        left: 25%;


        //Change grid postion for 600px and below
        @media only screen and (max-width: 800px) {
            grid-row: 1 / 2;
            grid-column: 1 / -1;
            left: 0;
        }

        //Icon container
        &--icons {
            margin-top: 4rem;
            display: flex;
            justify-content: start;

        }

        //Individual icons
