//Game section styling
.education__game {
    display: grid;
    grid-template-columns: repeat(12, 1fr);
    grid-template-rows: repeat(12, 1fr);
    justify-items: center;
    align-items: center;
    transition: all 1s;

    @media only screen and (max-width: 600px) {
        grid-row: 2 / 3;
        grid-column: 1 / 3;
    }

    h1 {
        font-size: 1.5rem;
        font-weight: 300;

        @media only screen and (max-width: 600px) {
            font-size: 2rem;
            font-weight: normal;
        }

    }

    &--new {
        font-size: 1.5rem;
        font-weight: 300;

        @media only screen and (max-width: 600px) {
            font-size: 2rem;
            font-weight: normal;
        }
    }

    //Tabs
    &--inst {
        cursor: pointer;
        grid-row: 2 / 3;
        grid-column: 8 / 9;
        font-size: 1.6rem;
        padding-bottom: .5rem;

        @media only screen and (max-width: 600px) {
            grid-column: 7 / 8;
        }
    }

    &--inst:hover {
        border-bottom: 2px solid #ffb1299c;
    }

    &--game:hover {
        border-bottom: 2px solid #ffb1299c;
    }

    &--game {
        cursor: pointer;
        grid-row: 2 / 3;
        grid-column: 6 / 7;
        font-size: 1.6rem;
        padding-bottom: .5rem;

        @media only screen and (max-width: 600px) {
