//Skill section styles
.skill-sec {
    min-height: 100vh;
    max-width: 100%;
    //Set grid
    display: grid;
    grid-template-columns: repeat(13, 1fr);
    grid-template-rows: repeat(12, 1fr);


    @media only screen and (max-width: 600px) {
        grid-template-rows: repeat(6, 1fr);
        grid-row-gap: 8rem;
        padding: 8rem 0;
    }


    &__card {
        background-color: rgba(0, 0, 0, 0.548);
        padding: 2rem;
        box-shadow: 0 .5rem 2rem rgba(255, 255, 255, 0.1);
        border-radius: .5rem;

        //Set grid
        display: grid;
        grid-template-columns: repeat(6, 1fr);
        grid-template-rows: repeat(6, 1fr);

        &--front {
            grid-row: 2 / 6;
            grid-column: 2 / 5;

            @media only screen and (max-width: 600px) {
                grid-row: 1 / 2;
                grid-column: 2 / 13;
            }

        }

        &--back {
            grid-row: 2 / 6;
            grid-column: 6 / 9;

            @media only screen and (max-width: 600px) {
                grid-row: 2 / 3;
                grid-column: 2 / 13;
            }

        }

        &--frame {
            grid-row: 2 / 6;
            grid-column: 10 / 13;

            @media only screen and (max-width: 600px) {
                grid-row: 3 / 4;
                grid-column: 2 / 13;
            }

        }

        &--dev {
            grid-row: 8 / 12;
            grid-column: 2 / 5;

            @media only screen and (max-width: 600px) {
                grid-row: 4 / 5;
                grid-column: 2 / 13;
            }

        }

        &--db {
            grid-row: 8 / 12;
            grid-column: 6 / 9;

            @media only screen and (max-width: 600px) {
                grid-row: 5 / 6;
                grid-column: 2 / 13;
            }

        }

        &--host {
            grid-row: 8 / 12;
            grid-column: 10 / 13;

            @media only screen and (max-width: 600px) {
                grid-row: 6 / 7;
                grid-column: 2 / 13;
            }

        }
    }

    //Title
    &__title {
        grid-row: 1 / 2;
        grid-column: 1 / -1;
        font-size: 2.2rem;
        font-weight: 300;
        margin-top: -2rem;

        @media only screen and (max-width: 600px) {
            font-size: 3rem;
            font-weight: 300;
        }
    }

    //Skill group
    &__group {
        font-size: 1.4rem;
        font-weight: normal;

        display: flex;
        align-items: center;
        margin-bottom: 1rem;
        transition: all .2s;
        text-decoration: none;
        color: white;

        @media only screen and (max-width: 600px) {
            font-size: 1.6rem;
            font-weight: normal;
        }

        &--1 {
            grid-row: 2 / 3;
            grid-column: 1 / -1;

        }

        &--2 {
            grid-row: 3 / 4;
            grid-column: 1 / -1;

        }

        &--3 {
            grid-row: 4 / 5;
            grid-column: 1 / -1;

        }

        &--4 {
            grid-row: 5 / 6;
            grid-column: 1 / -1;

        }

        &--5 {
            grid-row: 6 / 7;
            grid-column: 1 / -1;

        }
    }

    &__group:hover {
        cursor: pointer;
        transform: translateY(-.5rem);
    }
}

//Icons
.skills-icon {
    height: 2.5rem;
    width: 2.5rem;
    margin-right: 2rem;

    @media only screen and (max-width: 600px) {
       height: 3.5rem;
       width: 3.5rem;
    }
}

